import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
// const { kmeans } = require('../ml-kmeans');
const { kmeans } = require('ml-kmeans');
const BinPlot = ({data}) => {
    const svgRef = useRef();
    const [xAxis,setXAxis] = useState("");
    const [binSize,setBinSize] = useState("1");

    const handleXAxisChange = (event) => {
        setXAxis(event.target.value);
    };

    const handleBinSizeChange = (event) => {
        setBinSize(event.target.value);
    };

    useEffect(() => {
        if (data.length > 0) {
            if(!xAxis){
                setXAxis(Object.keys(data[0])[0]);
            }
            drawBinPlot();
        }
    }, [data, xAxis, binSize]);

    // calculate percentile
    const calculatePercentile = (value, array) => {
        const sortedArray = array.sort((a, b) => a - b);
        const index = sortedArray.indexOf(value);
        return (index / (sortedArray.length - 1)) * 100;
    };
    const drawBinPlot = () => {
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();

        // define width and height of the plot
        const width = 300;
        const height = 300;
        const margin = { top: 20, right: 50, bottom: 50, left: 50 };

        // define x scale
        const x = d3.scaleLinear()
            .domain(d3.extent(data, d => +d[xAxis])).nice()
            .range([margin.left, width - margin.right]);

        // apply k-means clustering to the data, remove NaN values
        const values = data.map(d => +d[xAxis]).filter(d => !isNaN(d));
        if (values.length === 0) {
            console.log('No valid data points for binning');
            return;
        }
        const uniqueValues = new Set(values);
        const effectiveBinSize = Math.min(+binSize, uniqueValues.size);

        const kmeansResults = kmeans(values.map(d => [d]), effectiveBinSize);
        // console.log(kmeansResults);

    

        // map data points to the bins
        const bins = Array.from({length: effectiveBinSize}, () => []);
        kmeansResults.clusters.forEach((cluster, i) => {
            if (cluster >=0 && cluster < effectiveBinSize) {
            bins[cluster].push(values[i]);
            }
        });

        // define y scale
        const y = d3.scaleBand()
            .domain(d3.range(effectiveBinSize))
            .range([height - margin.bottom, margin.top])
            .padding(0.1);
        
        // draw the x axis
        const xAxisG = g => g
        .attr('transform', `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(x).ticks(width / 80))
        .call(g => g.select('.domain').remove());

        // draw the y axis
        const yAxisG = g => g
        .attr('transform', `translate(${margin.left},0)`)
        .call(d3.axisLeft(y).tickFormat (i=> `Bin ${i+1}`))
        .call(g => g.select('.domain').remove());

        svg.append('g').call(xAxisG);
        svg.append('g').call(yAxisG);

        // draw the bins
        svg.append('g')
        .selectAll('rect')
        .data(bins)
        .join('rect')
        .attr('x', d => x(d3.min(d)))
        .attr('y', (d, i) => y(i))
        .attr('width', d => x(d3.max(d)) - x(d3.min(d)))
        .attr('height', y.bandwidth())
        .attr('fill', 'steelblue');
    }

    return (
    <div>
        <div className='bg-blue-100 w-1/2 text-sm'>
            <svg ref={svgRef} width={300} height={300}></svg>

            <label htmlFor="xAxis">X Axis:</label>
            <select id="xAxis" value={xAxis} onChange={handleXAxisChange}>
                {data[0] && Object.keys(data[0]).map(key => (
                    <option key={key} value={key}>{key}</option>
                ))}
            </select>
            <label>
                Bin Size:
                <input type="number" value={binSize} onChange={handleBinSizeChange} />
            </label>
        </div>
    </div>
    );
};

export default BinPlot;
