import React, { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';


const BinPlot = ({ data }) => {
    const svgRef = useRef();
    const [yAxis1, setYAxis1] = useState("");
    const [yAxis2, setYAxis2] = useState("");
    const [maxBins, setMaxBins] = useState(5); // Maximum number of bins

    const handleYAxis1Change = (event) => {
        setYAxis1(event.target.value);
    };

    const handleYAxis2Change = (event) => {
        setYAxis2(event.target.value);
    };

    const handleMaxBinsChange = (event) => {
        setMaxBins(parseInt(event.target.value, 10));
    };

    useEffect(() => {
        if (data.length > 0) {
            if (!yAxis1) {
                setYAxis1(Object.keys(data[0])[0]);
            }
            if (!yAxis2) {
                setYAxis2(Object.keys(data[0])[1]); // Initialize yAxis2 with another attribute
            }
            drawBinPlot();
        }
    }, [data, yAxis1, yAxis2, maxBins]);


       

    const drawBinPlot = () => {
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();
    
        const width = 600;
        const height = 400;
        const margin = { top: 20, right: 50, bottom: 50, left: 50 };

        // Filter data to remove entries where yAxis1 or yAxis2 values are null or undefined
        const filteredData = data.filter(d => d[yAxis1] != null && d[yAxis2] != null);

        // If there's no valid data after filtering, don't attempt to draw the plot
        if (filteredData.length === 0) {
            console.log('No valid data points for plotting');
            return;
        }

        
        // Define y1 and y2 scales for the two y-attributes
        const y1 = d3.scaleLinear()
            .domain(d3.extent(filteredData, d => +d[yAxis1])).nice()
            .range([height - margin.bottom, margin.top]);
    
        const y2 = d3.scaleLinear()
            .domain(d3.extent(filteredData, d => +d[yAxis2])).nice()
            .range([height - margin.bottom, margin.top]);
    
        // Define x scale to position the y-attributes
        const x = d3.scalePoint()
            .domain([yAxis1, yAxis2])
            .range([margin.left, width - margin.right]);
    
        // Calculate bins for y1 and y2
        const y1Bins = d3.histogram()
            .domain(y1.domain())
            .thresholds(y1.ticks(maxBins))(filteredData.map(d => +d[yAxis1]));
    
        const y2Bins = d3.histogram()
            .domain(y2.domain())
            .thresholds(y2.ticks(maxBins))(filteredData.map(d => +d[yAxis2]));
    
        // Draw y1 and y2 axes
        const yAxisG1 = g => g
            .attr('transform', `translate(${x(yAxis1)},0)`)
            .call(d3.axisLeft(y1));
    
        const yAxisG2 = g => g
            .attr('transform', `translate(${x(yAxis2)},0)`)
            .call(d3.axisRight(y2));
    
        svg.append('g').call(yAxisG1);
        svg.append('g').call(yAxisG2);
    
        // Draw the lines connecting bins between y1 and y2
        y1Bins.forEach((bin1, i) => {
            const bin2 = y2Bins[i];
    
            // For each bin in y1, find the corresponding bin in y2 and draw a line
            bin1.forEach((d, j) => {
                svg.append('line')
                    .attr('x1', x(yAxis1))
                    .attr('y1', y1(d))
                    .attr('x2', x(yAxis2))
                    .attr('y2', y2(bin2[j]))
                    .attr('stroke', 'steelblue')
                    .attr('stroke-width', 1.5)
                    .attr('opacity', 0.6);
            });
        });
    
        // Optionally, you can also draw circles at each bin position for clarity
        svg.append('g')
            .selectAll('circle')
            .data(data)
            .join('circle')
            .attr('cx', d => x(yAxis1))
            .attr('cy', d => y1(+d[yAxis1]))
            .attr('r', 3)
            .attr('fill', 'orange');
    
        svg.append('g')
            .selectAll('circle')
            .data(data)
            .join('circle')
            .attr('cx', d => x(yAxis2))
            .attr('cy', d => y2(+d[yAxis2]))
            .attr('r', 3)
            .attr('fill', 'orange');
    };
    
    return (
        <div>
            <div className='bg-blue-100 w-1/2 text-sm'>
                <svg ref={svgRef} width={600} height={400}></svg> {/* Adjusted SVG size */}
    
                <label htmlFor="yAxis1">Y Axis 1:</label>
                <select id="yAxis1" value={yAxis1} onChange={handleYAxis1Change}>
                    {data[0] && Object.keys(data[0]).map(key => (
                        <option key={key} value={key}>{key}</option>
                    ))}
                </select>
    
                <label htmlFor="yAxis2">Y Axis 2:</label>
                <select id="yAxis2" value={yAxis2} onChange={handleYAxis2Change}>
                    {data[0] && Object.keys(data[0]).map(key => (
                        <option key={key} value={key}>{key}</option>
                    ))}
                </select>
    
                <label>
                    Max Bins:
                    <input type="number" value={maxBins} onChange={handleMaxBinsChange} min="2" />
                </label>
            </div>
        </div>
    );
}
export default BinPlot;
